public class Main {
    public int minCut(String s) {
        if (s.length() == 1)
            return 0;

        int[] cut = new int[s.length()];
        boolean [][]palindrome = new boolean[s.length()][s.length()];

        for (int i = 1; i < s.length(); i++) {
            int min = i;
            for (int j = 0; j <= i; j++) {
                if (s.charAt(i) == s.charAt(j) && (i <= j + 1 || palindrome[i - 1][j + 1])) {
                    palindrome[i][j] = true;
                    min = Math.min(min, j == 0 ? 0 : 1 + cut[j - 1]);
                }
            }
            cut[i] = min;
        }
        return cut[s.length() - 1];
    }

    public static void main(String[] args) {
        Main m = new Main();

//        System.out.println(m.minCut("aab"));
//        System.out.println(m.minCut("aaa"));
        System.out.println(m.minCut("aabcde"));
//        System.out.println(m.minCut(""));
    }
}